import  React,{ Component } from 'react';
import Data from '../data.json';

class Form extends Component {
    constructor(props) {
        super(props);

        this.state = {
            id:'',
            maths: '',
            physics: '',
            chemistry: '',
            biology: '',
            english: ''
        }
    }

    handleUserChange = (event) => {
        this.setState({
            id: event.target.value
        })
    }

    getData() {
        Object.keys(Data).forEach(function(key) {
            if (Data[key]['id'] === this.state.id) {
                this.setState({
                    maths: Data[key]['maths'],
                    physics: Data[key]['physics'],
                    chemistry: Data[key]['chemistry'],
                    biology: Data[key]['biology'],
                    english: Data[key]['english']
                }) 
            }
        });
    }

    render() {
        return (
            <form>
                <div>
                    <label>Calculate the percentage level of a Person's mark</label>
                </div>
                <div>
                   <input type='text' value={this.state.id} onChange={this.handleUserChange}/>
                </div>
                <div>
                   <button type="submit" onClick={this.getData}>Get Data From Server</button>
                </div>
                <div>
                    <label>Subject Percentage</label>
                </div>
                <div>
                   <input type='text' value={this.state.maths} onChange={this.handleUserChange} placeholder='maths%'/>
                </div>
                <div>
                   <input type='text' value={this.state.physics} onChange={this.handleUserChange} placeholder='physics%'/>
                </div>
                <div>
                   <input type='text' value={this.state.chemistry} onChange={this.handleUserChange} placeholder='chemistry%'/>
                </div>
                <div>
                   <input type='text' value={this.state.biology} onChange={this.handleUserChange} placeholder='biology%'/>
                </div>
                <div>
                   <input type='text' value={this.state.english} onChange={this.handleUserChange} placeholder='english%'/>
                </div>
            </form>
        )
    }
} 

export default Form;
