import  React,{ Component } from 'react';

class Form extends Component {

    constructor(props) {
        super(props);

        this.state = {
            id:'',
            maths: '',
            physics: '',
            chemistry: '',
            biology: '',
            english: ''
        }
    }

    handleUserChange = (event) => {
        this.setState({
            id: event.target.value
        })
    }

    render() {
        return (
            <form>
                <div>
                    <label>Calculate the percentage level of a Person's mark</label>
                </div>
                <div>
                   <input type='text' value={this.state.id} onChange={this.handleUserChange}/>
                </div>
                <div>
                   <button>Get Data From Server</button>
                </div>
            </form>
        )
    }
} 

export default Form;
