import  React,{ Component } from 'react';
import { ProgressBar } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import Data from '../data.json';

class Form extends Component {
    constructor(props) {
        super(props);

        this.state = {
            id:'',
            maths: '',
            physics: '',
            chemistry: '',
            biology: '',
            english: ''
        }
        this.percentage = 0;
    }

    handleIdChange = (event) => {
        this.setState({
            id: event.target.value
        })
    }

    getData = (event) =>  {
        let TotalPercentage = 0;
        let newState = {maths: '', physics: '', chemistry: '', biology: '', english: ''};
        let id = this.state.id;

        Object.keys(Data).forEach(function(key) {
            if (Data[key]['id'] == id)
                newState = Data[key];
        });
        this.setState(newState);
        // TotalPercentage = (newState.maths + newState.physics + newState.chemistry + newState.biology + newState.english) /5;
        // console.log(TotalPercentage);
        this.totalPercentage = (newState.maths + newState.physics + newState.chemistry + newState.biology + newState.english) /5;;

        if (TotalPercentage >= 90)
            this.message = 'success';
        else if (TotalPercentage >= 70 && TotalPercentage < 90)
            this.message = 'info';
        else if (TotalPercentage > 30 && TotalPercentage < 70)
            this.message = 'warning';
        else if (TotalPercentage <= 30)
            this.message = 'danger';
    }

    render() {
        return (
            <form>
                <div>
                    <label>Calculate the percentage level of a Person's mark</label>
                </div>
                <div>
                   <input type='text' value={this.state.id} onChange={this.handleIdChange} placeholder='Student ID'/>
                </div>
                <div>
                   <button type='button' onClick={this.getData}>Get Data From Server</button>
                </div>
                <div>
                    <label>Subject Percentage</label>
                </div>
                <div>
                   <input type='text' value={this.state.maths} placeholder='maths%'/>
                </div>
                <div>
                   <input type='text' value={this.state.physics} placeholder='physics%'/>
                </div>
                <div>
                   <input type='text' value={this.state.chemistry} placeholder='chemistry%'/>
                </div>
                <div>
                   <input type='text' value={this.state.biology} placeholder='biology%'/>
                </div>
                <div>
                   <input type='text' value={this.state.english} placeholder='english%'/>
                </div>
                <div>
                    <label>Total Percentage</label>
                </div>
                <div className="progressBar">
                    <ProgressBar striped variant={this.message} now={this.percentage} label={`${this.percentage}% completed`}/>
                </div>
            </form>
        )
    }
} 

export default Form;
